(function () {
    'use strict';

    angular.module('demoApp.home')
        .controller('lotDetailsModalController', ['lot', '$rootScope', '$filter', 'modalServices', 'boundariesService', 'gmapServices', 'lotService', 'LEGAL_STATUS_SELECTION', 'TECHNICAL_STATUS_SELECTION', 'SBU_SELECTION', 'alertServices', lotDetailsModalController]);

    function lotDetailsModalController(lot, $rootScope, $filter, modalServices, boundariesService, gmapServices, lotService, LEGAL_STATUS_SELECTION, TECHNICAL_STATUS_SELECTION, SBU_SELECTION, alertServices) {
        var vm = this;

        var bounds;

        vm.maxDate = new Date();

        vm.form = {};
        vm.lot = {};

        vm.save = save;
        vm.close = close;

        vm.regionChanged = regionChanged;
        vm.provinceChanged = provinceChanged;
        vm.cityChanged = cityChanged;

        initialize();

        function initialize() {
            vm.lot = lot;

            if (vm.lot.details) {
                for (var key in vm.lot.details) {
                    if (!isNaN(vm.lot.details[key])) {
                        vm.lot.details[key] = $filter('number')(vm.lot.details[key], 2);
                    }
                }
            }

            // load regions typeid = 3
            boundariesService.loadBoundariesByType(3)
                .then(function(regions){
                    vm.regions = angular.copy(regions);
                });

            vm.sbuSelection = SBU_SELECTION;

            vm.lotStatusOptions = lotService.getLotStatusSelectionByRole($rootScope.currentUser.role);
            vm.legalStatusOptions = LEGAL_STATUS_SELECTION;
            vm.technicalStatusOptions = TECHNICAL_STATUS_SELECTION;
        }

        function save() {
            delete vm.lot.geom;

            lotService.saveLot(vm.lot, vm.lot.id)
                .then(function(newLotData){
                    // lotService.addLot(newLotData);
                    alertServices.showSuccess('Offer for lot #'+ newLotData.id+' updated successfully!');
                    modalServices.hideResolveModal(newLotData);
                });
        }

        function close() {
            modalServices.closeModal();
        }

        function panToBoundary (boundaryId) {
            boundariesService.getBoundaryDetails(boundaryId)
                .then(function (details) {
                    bounds = gmapServices.fitToBoundsByLatLngArray(details.geometry);

                    gmapServices.reverseGeocode(bounds.getCenter())
                        .then(function(addressList){
                            if (addressList.length) vm.lot.complete_address = addressList[0].formatted_address;
                        });
                });
        }

        function regionChanged (regionId) {
            // fit to bounds
            panToBoundary(regionId);

            // load province
            boundariesService.loadBoundaries(regionId)
                .then(function (provinces) {
                    vm.provinces = angular.copy(provinces);
                });
        }

        function provinceChanged (provinceId) {
            // fit to bounds
            panToBoundary(provinceId);

            // load cities
            boundariesService.loadBoundaries(provinceId)
                .then(function (cities) {
                    vm.cities = angular.copy(cities);
                });
        }

        function cityChanged (cityId) {
            // fit to bounds
            panToBoundary(cityId);
        }
    }
}());
(function(){
'use strict';

angular.module('demoApp.home')
    .factory('lotService', ['$rootScope', 'Lot', 'LOT_STATUS_SELECTION', 'gmapServices', '$q', 'modalServices', lotService]);

    function lotService ($rootScope, Lot, LOT_STATUS_SELECTION, gmapServices, $q, modalServices) {
        var service = {};

        var infowindow = gmapServices.createInfoWindow('');

        var lots = [];

        service.getLotStatusSelectionByRole = getLotStatusSelectionByRole;
        service.loadLots = loadLots;
        service.addLot = addLot;
        service.saveLot = saveLot;
        service.filterLot = filterLot;
        service.showLot = showLot;
        service.showLotDetails = showLotDetails;
        service.showReportIssueModal = showReportIssueModal;
        service.reportIssue = reportIssue;

        function getLotStatusSelectionByRole (role) {
            var lotStatuses = LOT_STATUS_SELECTION;

            switch(role) {
                case 'ADMIN':
                    return lotStatuses;
                    break;
                case 'CLAU SECRETARY':
                    return lotStatuses;
                    break;
                case 'LEGAL':
                    return lotStatuses.filter(function(status){
                        return ['FOR IC APPROVAL', 'ACQUIRE'].indexOf(status) === -1;
                    });
                    break;
                case 'MDC':
                    return lotStatuses.filter(function (status) {
                        return ['FOR IC APPROVAL', 'ACQUIRE'].indexOf(status) === -1;
                    });
                    break;
                case 'SBU BUSINESS DEV':
                    return lotStatuses.filter(function (status) {
                        return ['FOR DUE DILIGENCE', 'DUE DILIGENCE IN PROGRESS'].indexOf(status) === -1;
                    });
                    break;
                case 'CLAU ANALYST':
                    return lotStatuses;
                    break;
                case 'C&A':
                    return lotStatuses;
                    break;
                default:

            }
        }

        function loadLots () {
            Lot.getList()
                .then(function(response){
                    response.plain().forEach(function(item){
                        addLot(item);
                    });
                });
        }

        function addLot (item) {
            //console.log('add lot: ',item);
            var polygon = gmapServices.createPolygon(item.geom, '#2ecc71', true);

            polygon = angular.merge(polygon, item);

            polygon.center = gmapServices.getPolygonCenter(polygon);
            //
            polygon.content = '<div>';
            polygon.content += '<h4 class="no-margin text-muted padding-left-5">Project Name: ' + (item.project_name ? item.project_name : '') + '</h4>';
            polygon.content += '<h4 class="no-margin padding-left-5">Estate Name: <b>' + (item.estate_name ? item.estate_name : '') + '</b></h4>';
            polygon.content += '<h4 class="no-margin text-muted padding-left-5">SBU: ' + (item.sbu ? item.sbu : '') + '</h4>';
            polygon.content += '<h4 class="no-margin text-muted padding-left-5">Lot Status: ' + (item.lot_status ? item.lot_status : '') + '</h4>';
            /* Action Buttons */
            polygon.content += '<button id="show-lot-details-btn" data-lot-id="' + item.id + '" class="md-button md-raised">Show Details</button>';

            if (['MDC', 'LEGAL'].indexOf($rootScope.currentUser.role) > -1 && item.lot_status == 'DUE DILIGENCE IN PROGRESS') {
                polygon.content += '<button id="report-lot-issue-btn" data-lot-id="' + item.id + '" class="md-button md-raised md-warn" md-warn">Report Issue</button>';
            }

            polygon.content += '</div>';

            gmapServices.addListener(polygon, 'click', function(e){
                console.log('lot click: ',e);
                infowindow.open(gmapServices.map);
                infowindow.setPosition(e.latLng);
                //infowindow.setPosition(this.center);
                infowindow.setContent(this.content);
            });

            lots.push(polygon);

            gmapServices.fitToBoundsByPolygon(polygon);
        }

        // if lotId is not null perform update otherwise perform create
        function saveLot (lotData, lotId) {
            var dfd = $q.defer();

            if (lotId) { // update
                Lot.cast(lotId)
                    .customPUT(lotData)
                    .then(function (response) {
                        var resp = response.plain();

                        var index = _.findIndex(lots, {id: resp.lot.id});

                        if (index > -1) {
                            lots[index].lot_status = resp.lot.lot_status;
                            lots[index].legal_status = resp.lot.legal_status;
                            lots[index].technical_status = resp.lot.technical_status;
                        }

                        dfd.resolve(resp.lot);
                    }, function (error) {
                        dfd.reject(error);
                    });
            } else { // insert
                Lot.post(lotData)
                    .then(function (response) {
                        var resp = response.plain();
                        addLot(resp.lot);
                        dfd.resolve(resp.lot);
                    }, function (error) {
                        dfd.reject(error);
                    });
            }

            return dfd.promise;
        }

        function clearLots () {
            lots.forEach(function(lot){
                lot.setMap(null);
            });
            lots = [];
        }

        function filterLot (filterData) {
            var dfd = $q.defer();

            clearLots();

            Lot.getList(filterData)
                .then(function(response){
                    var resp = response.plain();

                    resp.forEach(function(item){
                       addLot(item);
                    });

                    dfd.resolve(resp);
                }, function (error){
                    dfd.reject(error);
                });

            return dfd.promise;
        }

        function showLot (lotId) {
            var lot = _.findWhere(lots, {id: lotId});

            if (lot) {
                gmapServices.panTo(lot.center);
                gmapServices.trigger(lot, 'click', {latLng: lot.center});
            }
        }


        function showLotDetails (lotId) {
            Lot.get(lotId)
                .then(function(response){
                    var resp = response.plain();
                    console.log('get lot details id = '+lotId, resp);

                    infowindow.close();

                    lots.forEach(function (item) {
                        if (item.id != lotId) item.setMap(null);
                        else gmapServices.fitToBoundsByPolygon(item);
                    });

                    modalServices.showLotDetailsModal(resp)
                        .finally(function(){
                            console.log('modal lot details finally');
                            lots.forEach(function (item) {
                                if (item.id != lotId) {
                                    item.setMap(gmapServices.map);
                                }
                            })
                        });
                });
        }

        function showReportIssueModal (lotId) {
            modalServices.showReportIssueModal($rootScope.currentUser, lotId)
                .finally(function () {
                    //console.log('modal lot details finally');
                    //lots.forEach(function (item) {
                    //    if (item.id != lotId) {
                    //        item.setMap(gmapServices.map);
                    //    }
                    //})
                });
        }

        function reportIssue (lotId, issueData) {
            var dfd = $q.defer();

            Lot.cast(lotId).customPOST(issueData, 'issues')
                .then(function(response){
                    var resp = response.plain();
                    console.log('report issue response ',resp);
                    dfd.resolve(resp);
                }, function(error){
                    dfd.reject(error);
                });

            return dfd.promise;
        }

        return service;
    }
}());
(function () {
    'use strict';

    angular.module('demoApp.home')
        .factory('Lot', ['Restangular', 'Upload', Lot]);

    function Lot(Restangular, Upload) {
        var myModel = Restangular.all('lots');

        var resource = {

            cast: function (myid) {
                return Restangular.restangularizeElement(null, {id: myid}, 'lots');
            },

            uploadShapeFile: function (fileParam) {
                var uploadUrl = myModel.getRestangularUrl() + '/' + 'upload';
                return Upload.upload({
                    url: uploadUrl,
                    method: 'POST',
                    data: {file: fileParam}
                });
            }
        };

        angular.merge(myModel, resource);

        return myModel;
    }
}());
